import React, { useEffect, useState } from 'react'
import {
	collection,
	addDoc,
	getDocs,
	updateDoc,
	deleteDoc,
	doc,
	serverTimestamp,
} from 'firebase/firestore'
import {
	ref,
	uploadBytes,
	getDownloadURL,
	deleteObject,
} from 'firebase/storage'
import { db } from './firebaseConfig'
import { storage } from './firebaseConfig'

function App() {
	const [imageFile, setImageFile] = useState(null)
	const [bought, setBought] = useState('')
	const [given, setGiven] = useState('')
	const [date, setDate] = useState('')
	const [profiles, setProfiles] = useState([])

	const cigarettes = ['Winston Blue', 'LD Compact', 'Kent', 'Parliament']

	const handleSubmit = async (e) => {
		e.preventDefault()
		if (!imageFile || !bought || !given || !date)
			return alert('Заполните все поля')

		const imageRef = ref(storage, `profiles/${Date.now()}_${imageFile.name}`)
		await uploadBytes(imageRef, imageFile)
		const imageUrl = await getDownloadURL(imageRef)

		await addDoc(collection(db, 'profiles'), {
			photoUrl: imageUrl,
			bought,
			given,
			createdAt: new Date(date),
			usageCount: 1,
			lastUsedAt: null,
			imagePath: imageRef.fullPath,
		})

		setImageFile(null)
		setBought('')
		setGiven('')
		setDate('')
		fetchProfiles()
	}

	const fetchProfiles = async () => {
		const snapshot = await getDocs(collection(db, 'profiles'))
		const now = new Date()

		const filtered = snapshot.docs
			.map((doc) => ({ id: doc.id, ...doc.data() }))
			.filter((profile) => {
				if (profile.usageCount >= 3) return false
				if (!profile.lastUsedAt) return true

				const lastUsed = new Date(profile.lastUsedAt.seconds * 1000)
				const diff = (now - lastUsed) / (1000 * 60 * 60 * 24) // days
				return diff >= 30
			})

		setProfiles(filtered)
	}

	const handleUse = async (profile) => {
		const docRef = doc(db, 'profiles', profile.id)
		const newCount = (profile.usageCount || 1) + 1

		if (newCount >= 3) {
			// Удаляем документ и фото
			await deleteDoc(docRef)
			if (profile.imagePath) {
				const imgRef = ref(storage, profile.imagePath)
				await deleteObject(imgRef)
			}
		} else {
			await updateDoc(docRef, {
				usageCount: newCount,
				lastUsedAt: new Date(),
			})
		}

		fetchProfiles()
	}

	useEffect(() => {
		fetchProfiles()
	}, [])

	return (
		<div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
			<h1>Добавить анкету</h1>
			<form onSubmit={handleSubmit}>
				<input type='file' onChange={(e) => setImageFile(e.target.files[0])} />
				<div>
					<label>Что купил:</label>
					<select value={bought} onChange={(e) => setBought(e.target.value)}>
						<option value=''>Выбери</option>
						{cigarettes.map((c) => (
							<option key={c} value={c}>
								{c}
							</option>
						))}
					</select>
				</div>
				<div>
					<label>Что выдал:</label>
					<select value={given} onChange={(e) => setGiven(e.target.value)}>
						<option value=''>Выбери</option>
						{cigarettes.map((c) => (
							<option key={c} value={c}>
								{c}
							</option>
						))}
					</select>
				</div>
				<div>
					<label>Дата проведения:</label>
					<input
						type='date'
						value={date}
						onChange={(e) => setDate(e.target.value)}
					/>
				</div>
				<button type='submit'>Сохранить</button>
			</form>

			<hr />

			<h2>Доступные анкеты</h2>
			{profiles.length > 0 ? (
				profiles.map((p) => (
					<div
						key={p.id}
						style={{
							border: '1px solid #ccc',
							margin: '10px 0',
							padding: '10px',
						}}
					>
						<img
							src={p.photoUrl}
							alt='Анкета'
							style={{
								width: '100%',
								maxHeight: '300px',
								objectFit: 'contain',
							}}
						/>
						<p>
							<strong>Купил:</strong> {p.bought}
						</p>
						<p>
							<strong>Выдал:</strong> {p.given}
						</p>
						<p>
							<strong>Дата:</strong>{' '}
							{new Date(p.createdAt.seconds * 1000).toLocaleDateString()}
						</p>
						<p>
							<strong>Использовано:</strong> {p.usageCount} раз(а)
						</p>
						<button onClick={() => handleUse(p)}>Использовать</button>
					</div>
				))
			) : (
				<p>Нет доступных анкет</p>
			)}
		</div>
	)
}

export default App
